let _af = $(".af")
_af.on("click", function() {
    $(this).next().children().addClass("opmap")
    let mapX = Number($(this).next().children().attr("data-sateX"))
    let mapY = Number($(this).next().children().attr("data-sateY"))
    var map = new BMap.Map("allmap"); // 创建Map实例
    var point = new BMap.Point(mapX, mapY);
    map.centerAndZoom(point, 17); // 初始化地图,用城市名设置地图中心点
    map.enableScrollWheelZoom(); // 缩放功能
    var marker = new BMap.Marker(point); // 创建标注
    map.addOverlay(marker); // 将标注添加到地图中
    marker.setAnimation(BMAP_ANIMATION_BOUNCE);
})

// 百度地图API功能




$(window).bind("load resize", function() {
    let screenDom = document.documentElement.clientWidth;
    let _img1 = 20,
        _mf = 80,
        _mf2 = 90


    $(".userNeav").css({
        "top": screenDom / _img1,
        "fontSize": screenDom / _mf
    })
    $(".userNeav p:eq(1)").css({

        "fontSize": screenDom / _mf2
    })
    $(".userNeav img").css({
        "width": screenDom / _img1,
    })


    $(".homeNeav-any p").css({
        "fontSize": screenDom / _mf
    })
    if (screenDom < 900) {
        $(".homebanner").css("width", "100%")
        $(".mainimg").css("width", "68%")
        $(".homeNeav-any").css({
            "height": screenDom / 1.5
        })
    } else {
        $(".homebanner").css("width", "74%")
        $(".mainimg").css("width", "80%")
        $(".homeNeav-any").css({
            "height": screenDom / 2.35
        })
    }
    $(".user-imgone img").css({
        "width": screenDom / _img1,
        "left": screenDom / 20
    })


    $(".user-imgone").css({
        "height": $(".homeNeav-any").height()
    })


    $(".homeall").css({
        "height": $(".homeNeav").height()
    })
    $(".homeall .eb").css({
        "fontSize": screenDom / 75
    })
    $(".homeall .el").css({
        "fontSize": screenDom / 80
    })
    $(".homeall .eg").css({
        "fontSize": screenDom / 85
    })
    $(".mainimg").css({
        "height": screenDom / 5
    })

    // 点击大图打开蒙板
    $(".mainimg").on("click", function() {
            $(this).parent().parent().parent().parent().parent().parent().parent().children().first().attr("src", $(this).attr("src"))
            $(this).parent().parent().parent().parent().parent().parent().parent().children().first().css("width", screenDom / 2.2)
            $(this).parent().parent().parent().parent().parent().parent().parent().children().first().attr("class", "showa")
        })
        // 点击小图，切换大图
    $(".someimg").on("click", function() {
        $(this).parent().prev().attr("src", $(this).attr("src"))
    })



    let _theArr = []
    var _theEl = document.querySelectorAll(".clike")
    _theEl.forEach(function(el, index) {
        _theArr.push({
            clikeId: index,
            mathco: Number(el.children[3].innerHTML)
        })
    })

    $(".clike").on("click", function() {
            let _clikeId = $(this).parent().parent().parent().index()
            _theArr.forEach(function(el) {
                    if (_clikeId == el.clikeId) {
                        el.mathco += 1
                    }
                })
                // 此时已经获取到了每条动态的点赞数，可以存储可以渲染
            console.log(_theArr)
                // 点赞数前端显示加1
            $(this).children().first().next().next().next().html(Number($(this).children().first().next().next().next().html()) + 1)
                // 改变爱心颜色
            $(this).children().first().attr("class", "lastimg")
            $(this).children().first().next().removeAttr("class")
                // 显示+1的效果
            $(this).children().first().next().next().addClass("nowjump")
        })
        // 鼠标拿出后恢复
    $(".clike").on("mouseleave", function() {

        $(this).children().first().removeAttr("class")
        $(this).children().first().next().attr("class", "lastimg")
        $(this).children().first().next().next().removeClass("nowjump")
    })

})